import React, { useState, useEffect } from 'react';
import { LoadScript, GoogleMap, Marker, InfoWindow } from '@react-google-maps/api';
import { getCurrentLocation } from '../../services/mapsService';
import './Map.css';

const mapContainerStyle = {
  width: '100%',
  height: '100%'
};

const defaultMapOptions = {
  disableDefaultUI: true,
  zoomControl: true,
};

const defaultLocation = { lat: 40.7128, lng: -74.0060 }; // New York as fallback

const PLACE_TYPES = {
    museum: { name: 'Museums', color: 'blue', icon: 'http://maps.google.com/mapfiles/ms/icons/blue-dot.png' },
    park: { name: 'Parks', color: 'green', icon: 'http://maps.google.com/mapfiles/ms/icons/green-dot.png' },
    restaurant: { name: 'Restaurants', color: 'yellow', icon: 'http://maps.google.com/mapfiles/ms/icons/yellow-dot.png' },
    tourist_attraction: { name: 'Landmarks', color: 'purple', icon: 'http://maps.google.com/mapfiles/ms/icons/purple-dot.png' },
    event: { name: 'Events', color: 'red', icon: 'http://maps.google.com/mapfiles/ms/icons/red-dot.png' }
  };
  
  const libraries = ['places']; // Defined as constant to prevent LoadScript re-render
  
  const MapContainer = ({ events = [], places = [], onUserLocation, onPlacesLoaded }) => {
    const [map, setMap] = useState(null);
    const [userLocation, setUserLocation] = useState(defaultLocation);
    const [selectedMarker, setSelectedMarker] = useState(null);
    const [mapError, setMapError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [activeFilters, setActiveFilters] = useState({
      museum: true,
      park: true,
      restaurant: true,
      tourist_attraction: true,
      event: true
    });
  
    // Initialize location
    useEffect(() => {
      const initLocation = async () => {
        try {
          const location = await getCurrentLocation();
          setUserLocation(location);
          if (onUserLocation) onUserLocation(location);
        } catch (error) {
          console.error("Location error:", error);
          setMapError("Using default location");
          setUserLocation(defaultLocation);
          if (onUserLocation) onUserLocation(defaultLocation);
        } finally {
          setLoading(false);
        }
      };
      initLocation();
    }, [onUserLocation]);
  
    // Load places when map is ready
    useEffect(() => {
      if (!map || !window.google || !userLocation) return;
  
      const service = new window.google.maps.places.PlacesService(map);
      const fetchPlaces = async (type) => {
        return new Promise((resolve) => {
          service.nearbySearch({
            location: new window.google.maps.LatLng(userLocation.lat, userLocation.lng),
            radius: 2000,
            type: type
          }, (results, status) => {
            resolve(status === 'OK' ? results.map(r => ({ ...r, type })) : []);
          });
        });
      };
  
      const loadAllPlaces = async () => {
        try {
          const placeTypes = Object.keys(PLACE_TYPES).filter(t => t !== 'event');
          const allPlaces = await Promise.all(placeTypes.map(fetchPlaces));
          const combined = allPlaces.flat();
          if (onPlacesLoaded) onPlacesLoaded(combined);
        } catch (error) {
          console.error("Error loading places:", error);
        }
      };
  
      loadAllPlaces();
    }, [map, userLocation, onPlacesLoaded]);
  
    // Filter events with valid locations
    const validEvents = events.filter(event => 
      event.latitude && event.longitude &&
      !isNaN(event.latitude) && !isNaN(event.longitude)
    );
  
    const toggleFilter = (type) => {
      setActiveFilters(prev => ({ ...prev, [type]: !prev[type] }));
    };
  
    if (loading) return <div className="loading-overlay">Loading map...</div>;
    if (mapError) return <div className="map-error">{mapError}</div>;
  
    return (
      <div className="map-wrapper">
        {/* Filter Controls */}
        <div className="map-filters">
          {Object.entries(PLACE_TYPES).map(([type, { name, color }]) => (
            <button
              key={type}
              className={`filter-btn ${activeFilters[type] ? 'active' : ''}`}
              style={{ backgroundColor: activeFilters[type] ? color : '#ccc' }}
              onClick={() => toggleFilter(type)}
            >
              {name}
            </button>
          ))}
        </div>
  
        <LoadScript
          googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}
          libraries={libraries} // Use constant here
        >
          <GoogleMap
            mapContainerStyle={mapContainerStyle}
            center={userLocation}
            zoom={13}
            options={defaultMapOptions}
            onLoad={map => setMap(map)}
          >
            {/* User Location */}
            <Marker
              position={userLocation}
              icon={{
                url: 'http://maps.google.com/mapfiles/ms/icons/blue-dot.png'
              }}
            />
  
            {/* Filtered Events */}
            {activeFilters.event && validEvents.map(event => (
              <Marker
                key={`event-${event.id}`}
                position={{ lat: Number(event.latitude), lng: Number(event.longitude) }}
                onClick={() => setSelectedMarker({ ...event, type: 'event' })}
                icon={{
                  url: PLACE_TYPES.event.icon
                }}
              />
            ))}
  
            {/* Filtered Places */}
            {places.filter(p => activeFilters[p.type]).map(place => (
              <Marker
                key={`place-${place.place_id}`}
                position={place.geometry.location}
                onClick={() => setSelectedMarker(place)}
                icon={{
                  url: PLACE_TYPES[place.type]?.icon || PLACE_TYPES.tourist_attraction.icon
                }}
              />
            ))}
  
            {/* Info Window */}
            {selectedMarker && (
              <InfoWindow
                position={
                  selectedMarker.geometry?.location || {
                    lat: Number(selectedMarker.latitude),
                    lng: Number(selectedMarker.longitude)
                  }
                }
                onCloseClick={() => setSelectedMarker(null)}
              >
                <div>
                  <h3>{selectedMarker.name}</h3>
                  <p>{selectedMarker.vicinity || selectedMarker.location}</p>
                  {selectedMarker.rating && <p>â˜… {selectedMarker.rating}</p>}
                  {selectedMarker.date && <p>ðŸ“… {selectedMarker.date}</p>}
                  <p>Type: {PLACE_TYPES[selectedMarker.type]?.name || 'Event'}</p>
                </div>
              </InfoWindow>
            )}
          </GoogleMap>
        </LoadScript>
      </div>
    );
  };
  
  export default MapContainer;
