import React, { useState, useEffect, useCallback } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';
import axios from 'axios';
import './App.css';

// Set your Google Maps API key here
const GOOGLE_MAPS_API_KEY = 'AIzaSyCiGYMylud9qEYCFLHTVNtmX8yhk3sxswk';

const mapContainerStyle = {
  width: '100%',
  height: '100%'
};

const center = {
  lat: 0,
  lng: 0
};

function App() {
  const [map, setMap] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [events, setEvents] = useState([]);
  const [places, setPlaces] = useState([]);
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Initialize map and get user location
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
          fetchNearbyPlaces(position.coords.latitude, position.coords.longitude);
        },
        (err) => {
          console.error("Error getting location: ", err);
          setError("Could not get your location. Using default location.");
          // Default to a known location if geolocation fails
          setUserLocation({ lat: 40.7128, lng: -74.0060 }); // New York
          fetchNearbyPlaces(40.7128, -74.0060);
        }
      );
    } else {
      setError("Geolocation is not supported by this browser.");
      setUserLocation({ lat: 40.7128, lng: -74.0060 }); // New York
      fetchNearbyPlaces(40.7128, -74.0060);
    }
  }, []);

  // Fetch events from your API
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get('https://events-api-ydqd.onrender.com/api/events');
        setEvents(response.data);
      } catch (error) {
        console.error('Error fetching events:', error);
        setError('Failed to load events');
      } finally {
        setLoading(false);
      }
    };

    fetchEvents();
  }, []);

  // Fetch nearby places using Google Places API
  const fetchNearbyPlaces = useCallback((lat, lng) => {
    if (!window.google) return;

    const service = new window.google.maps.places.PlacesService(document.createElement('div'));
    const request = {
      location: new window.google.maps.LatLng(lat, lng),
      radius: '5000',
      type: ['park', 'tourist_attraction', 'amusement_park']
    };

    service.nearbySearch(request, (results, status) => {
      if (status === window.google.maps.places.PlacesServiceStatus.OK) {
        setPlaces(results);
      } else {
        console.error('Places API error:', status);
      }
    });
  }, []);

  const onMapLoad = useCallback((map) => {
    setMap(map);
  }, []);

  const onMarkerClick = (place) => {
    setSelectedPlace(place);
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="app">
      <header className="header">
        <h1>EventsExplorer</h1>
        {/* Rest of your header */}
      </header>

      {/* Your search and navigation components */}

      <div className="main-content">
        <div className="map-container">
          <LoadScript
            googleMapsApiKey={GOOGLE_MAPS_API_KEY}
            libraries={['places']}
          >
            <GoogleMap
              mapContainerStyle={mapContainerStyle}
              center={userLocation || center}
              zoom={13}
              onLoad={onMapLoad}
            >
              {/* User location marker */}
              {userLocation && (
                <Marker
                  position={userLocation}
                  icon={{
                    url: 'http://maps.google.com/mapfiles/ms/icons/blue-dot.png'
                  }}
                />
              )}

              {/* Event markers */}
              {events.map(event => (
                <Marker
                  key={`event-${event.id}`}
                  position={{ lat: event.latitude, lng: event.longitude }}
                  onClick={() => onMarkerClick({
                    ...event,
                    name: event.name,
                    location: event.location
                  })}
                />
              ))}

              {/* Place markers */}
              {places.map(place => (
                <Marker
                  key={`place-${place.place_id}`}
                  position={place.geometry.location}
                  onClick={() => onMarkerClick(place)}
                />
              ))}

              {/* InfoWindow for selected place/event */}
              {selectedPlace && (
                <InfoWindow
                  position={
                    selectedPlace.geometry 
                      ? selectedPlace.geometry.location 
                      : { lat: selectedPlace.latitude, lng: selectedPlace.longitude }
                  }
                  onCloseClick={() => setSelectedPlace(null)}
                >
                  <div>
                    <h3>{selectedPlace.name}</h3>
                    <p>{selectedPlace.vicinity || selectedPlace.location}</p>
                    {selectedPlace.rating && (
                      <p>Rating: {selectedPlace.rating} ({selectedPlace.user_ratings_total || 0} reviews)</p>
                    )}
                  </div>
                </InfoWindow>
              )}
            </GoogleMap>
          </LoadScript>
        </div>

        {/* Your events and activities list */}
      </div>
    </div>
  );
}

export default App;
